using System.Text;
using Rockabilly.Common;
using Rockabilly.Common.HtmlEffects;

namespace Rockabilly.CoarseGrind
{
	public class CoarseGrindLogSystem : HtmlLogSystem
	{

		public override string GetHtmlLogHeader(string headline)
		{
			StringBuilder result = new StringBuilder();
			result.Append("<table>");
			result.Append(HTML__ROW_START);
			result.Append(HTML__TABLE_BEGIN_DATA_CELL);
			result.Append(HEADERICON.ToString());
			result.Append(HTML__TABLE_DIVISION);
			result.Append(HTML__SPACE);
			result.Append(HTML__TABLE_DIVISION);
			result.Append(HTML__SPACE);
			result.Append(HTML__TABLE_DIVISION);
			result.Append("<center><h1>");
			result.Append(headline);
			result.Append("</h1>Initiated ");
			result.Append(PlainTimeStamp);
			result.Append("<br><small><i>Powered by Coarse Grind</i></small></center>");
			result.Append(HTML__TABLE_END_DATA_CELL);
			result.Append(HTML__TABLE_END);
			result.Append(HTML__TABLE_START);

			return result.ToString();
		}

		public override InlineImage CriticalFailureIcon
		{
			get
			{
				return CRITICALFAIL;
			}
		}

		public override InlineImage PassIcon
		{
			get
			{
				return PASSNOTICE;
			}
		}

		public override InlineImage FailIcon
		{
			get
			{
				return FAILNOTICE;
			}
		}

		public override string HtmlStyle
		{
			get
			{
				return HTML__TABLE_STYLE;
			}
		}


		public InlineImage StopSignIcon
		{
			get
			{
				return STOPSIGN;
			}
		}

		public InlineImage WarningIcon
		{
			get
			{
				return WARNNOTICE;
			}
		}

		public InlineImage InfoIcon
		{
			get
			{
				return INFOICON;
			}
		}


		const String HTML__TABLE_STYLE = "table.rockabilly_log_system {border-spacing: 0; border:1px solid #000}\ntable.rockabilly_log_system td, th {padding: 0 0.2em}\ntable.rockabilly_log_system td, th {border:none}\ntable.rockabilly_log_system tr:nth-child(odd) {color: #000; background: #FFF7E6}\ntable.rockabilly_log_system tr:nth-child(even) {color: #000; background: #E6FFEE}\ntable.rockabilly_logged_exception {border-spacing: 0; border:2px solid #000}\ntable.rockabilly_logged_exception td, th {padding: 0 0.2em}\ntable.rockabilly_logged_exception td, th {border:none}\ntable.rockabilly_logged_exception tr:nth-child(odd) {color: #000; background: #FFFC08}\ntable.rockabilly_logged_exception tr:nth-child(even) {color: #000; background: #FFEB08}";

		private static readonly InlineImage HEADERICON = new Icon_Header();
		private static readonly InlineImage PASSNOTICE = new Icon_PassNotice();
		private static readonly InlineImage STOPSIGN = new Icon_StopSign();
		private static readonly InlineImage FAILNOTICE = new Icon_FailNotice();
		private static readonly InlineImage WARNNOTICE = new Icon_WarnNotice();
		private static readonly InlineImage INFOICON = new Icon_Info();
		private static readonly InlineImage CRITICALFAIL = new Icon_CriticalFailure();
		private static readonly InlineImage SECTIONSTART = new Image_SectionStart();
		private static readonly InlineImage SECTIONEND = new Image_SectionEnd();






		public override List<InlineImage> AllInlineImages
		{
			get
			{
				return imageBank;
			}
		}



		private static readonly List<InlineImage> imageBank = new List<InlineImage>();

		static CoarseGrindLogSystem()
		{
			imageBank.Add(HEADERICON);
			imageBank.Add(PASSNOTICE);
			imageBank.Add(STOPSIGN);
			imageBank.Add(FAILNOTICE);
			imageBank.Add(WARNNOTICE);
			imageBank.Add(INFOICON);
			imageBank.Add(CRITICALFAIL);
			imageBank.Add(SECTIONSTART);
			imageBank.Add(SECTIONEND);
		}


		internal string HTML__SECTIONSTART
		{
			get
			{
				return SECTIONSTART.ToString();
			}
		}

		internal string HTML__SECTIONEND
		{
			get
			{
				return SECTIONEND.ToString();
			}
		}
	}

